import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { 
  Scale,
  Home,
  FileText,
  ClipboardList,
  Users,
  Settings,
  PlusCircle,
  BarChart3
} from 'lucide-react';

const Sidebar = () => {
  const location = useLocation();
  const { isAdmin } = useAuth();

  const isActive = (path) => {
    return location.pathname === path;
  };

  const menuItems = [
    {
      title: 'Dashboard',
      icon: <Home className="w-5 h-5" />,
      path: '/dashboard',
      roles: ['user', 'admin']
    },
    {
      title: 'Policies',
      icon: <FileText className="w-5 h-5" />,
      path: '/policies',
      roles: ['user', 'admin']
    },
    {
      title: 'My Claims',
      icon: <ClipboardList className="w-5 h-5" />,
      path: '/claims',
      roles: ['user', 'admin']
    },
    {
      title: 'File New Claim',
      icon: <PlusCircle className="w-5 h-5" />,
      path: '/claims/new',
      roles: ['user', 'admin']
    }
  ];

  const adminMenuItems = [
    {
      title: 'User Management',
      icon: <Users className="w-5 h-5" />,
      path: '/admin/users',
      roles: ['admin']
    },
    {
      title: 'Analytics',
      icon: <BarChart3 className="w-5 h-5" />,
      path: '/admin/analytics',
      roles: ['admin']
    },
    {
      title: 'Settings',
      icon: <Settings className="w-5 h-5" />,
      path: '/admin/settings',
      roles: ['admin']
    }
  ];

  const renderMenuItem = (item) => {
    const active = isActive(item.path);
    
    return (
      <Link
        key={item.path}
        to={item.path}
        className={`flex items-center space-x-3 px-4 py-3 rounded-lg transition-all duration-200 ${
          active
            ? 'bg-blue-600 text-white shadow-lg'
            : 'text-gray-300 hover:bg-gray-700 hover:text-white'
        }`}
      >
        {item.icon}
        <span className="font-medium">{item.title}</span>
      </Link>
    );
  };

  return (
    <div className="bg-gray-800 text-white w-64 min-h-screen flex flex-col">
      {/* Logo Section */}
      <div className="p-6 border-b border-gray-700">
        <Link to="/dashboard" className="flex items-center space-x-3">
          <Scale className="w-8 h-8 text-blue-400" />
          <div>
            <h1 className="text-xl font-bold">Legal Partners</h1>
            <p className="text-sm text-gray-400">Management Portal</p>
          </div>
        </Link>
      </div>

      {/* Navigation Menu */}
      <nav className="flex-1 px-4 py-6">
        <div className="space-y-2">
          {/* Main Menu Items */}
          <div className="mb-8">
            <h3 className="text-xs font-semibold text-gray-400 uppercase tracking-wider mb-3">
              Main Menu
            </h3>
            {menuItems.map(renderMenuItem)}
          </div>

          {/* Admin Menu Items */}
          {isAdmin() && (
            <div className="mb-8">
              <h3 className="text-xs font-semibold text-gray-400 uppercase tracking-wider mb-3">
                Administration
              </h3>
              {adminMenuItems.map(renderMenuItem)}
            </div>
          )}
        </div>
      </nav>

      {/* Quick Stats */}
      <div className="p-4 border-t border-gray-700">
        <div className="bg-gray-700 rounded-lg p-4">
          <h4 className="text-sm font-semibold text-gray-200 mb-3">Quick Stats</h4>
          <div className="space-y-2">
            <div className="flex justify-between text-sm">
              <span className="text-gray-400">Active Policies</span>
              <span className="text-white font-medium">24</span>
            </div>
            <div className="flex justify-between text-sm">
              <span className="text-gray-400">Pending Claims</span>
              <span className="text-yellow-400 font-medium">8</span>
            </div>
            {isAdmin() && (
              <div className="flex justify-between text-sm">
                <span className="text-gray-400">Total Users</span>
                <span className="text-blue-400 font-medium">156</span>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Help Section */}
      <div className="p-4 border-t border-gray-700">
        <div className="text-center">
          <p className="text-xs text-gray-400 mb-2">Need Help?</p>
          <button className="w-full bg-blue-600 hover:bg-blue-700 text-white text-sm py-2 px-4 rounded-lg transition-colors">
            Contact Support
          </button>
        </div>
      </div>

      {/* Footer */}
      <div className="p-4 border-t border-gray-700">
        <div className="text-center text-xs text-gray-400">
          <p>&copy; 2025 Legal Partners</p>
          <p>Version 1.0.0</p>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
