import React, { useState, useEffect } from 'react';
import { ChevronLeft, ChevronRight, Play, Pause } from 'lucide-react';

const Slider = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isPlaying, setIsPlaying] = useState(true);

  const slides = [
    {
      id: 1,
      image: "https://images.unsplash.com/photo-1589829545856-d10d557cf95f?ixlib=rb-4.0.3&auto=format&fit=crop&w=2070&q=80",
      title: "Excellence in Legal Representation",
      subtitle: "Trusted by thousands of clients nationwide",
      description: "Our experienced team delivers outstanding results with personalized attention to every case."
    },
    {
      id: 2,
      image: "https://images.unsplash.com/photo-1505664194779-8beaceb93744?ixlib=rb-4.0.3&auto=format&fit=crop&w=2070&q=80",
      title: "Modern Legal Solutions",
      subtitle: "Technology meets traditional legal expertise",
      description: "Streamlined processes and digital tools for efficient case management and client communication."
    },
    {
      id: 3,
      image: "https://images.unsplash.com/photo-1521791136064-7986c2920216?ixlib=rb-4.0.3&auto=format&fit=crop&w=2069&q=80",
      title: "Client Success Stories",
      subtitle: "Your success is our priority",
      description: "Read testimonials from our satisfied clients who achieved successful outcomes with our legal guidance."
    },
    {
      id: 4,
      image: "https://images.unsplash.com/photo-1556761175-b413da4baf72?ixlib=rb-4.0.3&auto=format&fit=crop&w=2074&q=80",
      title: "Comprehensive Legal Services",
      subtitle: "One firm, multiple practice areas",
      description: "From corporate law to personal injury, we provide expert legal counsel across diverse practice areas."
    }
  ];

  // Auto-advance slides
  useEffect(() => {
    if (!isPlaying) return;

    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);

    return () => clearInterval(timer);
  }, [isPlaying, slides.length]);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  const togglePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  return (
    <div className="slider-container">
      {/* Slides Container */}
      <div 
        className="slider-wrapper"
        style={{ transform: `translateX(-${currentSlide * 100}%)` }}
      >
        {slides.map((slide, index) => (
          <div
            key={slide.id}
            className="slider-slide"
          >
            {/* Background Image */}
            <div
              className="slider-background"
              style={{ backgroundImage: `url(${slide.image})` }}
            >
              {/* Overlay */}
              <div className="slider-overlay"></div>
            </div>

            {/* Content */}
            <div className="slider-content">
              <div className="container mx-auto px-4">
                <div className="slider-text">
                  <div className="slider-animation">
                    <h2 className="slider-subtitle">
                      {slide.subtitle}
                    </h2>
                    <h1 className="slider-title">
                      {slide.title}
                    </h1>
                    <p className="slider-description">
                      {slide.description}
                    </p>
                    <div className="slider-actions">
                      <button className="btn btn-primary btn-lg">
                        Learn More
                      </button>
                      <button className="btn btn-secondary btn-lg slider-btn-secondary">
                        Contact Us
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Navigation Controls */}
      <div className="slider-navigation">
        {/* Previous Button */}
        <button
          onClick={prevSlide}
          className="slider-nav-btn slider-nav-prev"
          aria-label="Previous slide"
        >
          <ChevronLeft className="w-6 h-6" />
        </button>

        {/* Next Button */}
        <button
          onClick={nextSlide}
          className="slider-nav-btn slider-nav-next"
          aria-label="Next slide"
        >
          <ChevronRight className="w-6 h-6" />
        </button>
      </div>

      {/* Bottom Controls */}
      <div className="slider-controls">
        {/* Slide Indicators */}
        <div className="slider-indicators">
          {slides.map((_, index) => (
            <button
              key={index}
              onClick={() => goToSlide(index)}
              className={`slider-indicator ${
                index === currentSlide ? 'active' : ''
              }`}
              aria-label={`Go to slide ${index + 1}`}
            />
          ))}
        </div>

        {/* Play/Pause Button */}
        <button
          onClick={togglePlayPause}
          className="slider-play-pause"
          aria-label={isPlaying ? 'Pause slideshow' : 'Play slideshow'}
        >
          {isPlaying ? (
            <Pause className="w-4 h-4" />
          ) : (
            <Play className="w-4 h-4" />
          )}
        </button>
      </div>

      {/* Progress Bar */}
      <div className="slider-progress-container">
        <div 
          className={`slider-progress-bar ${isPlaying ? 'animated' : ''}`}
          key={currentSlide}
        />
      </div>

      {/* Slider Styles */}
      <style jsx>{`
